<?php defined('BASEPATH')OR exit('No direct script access allowed');class Urologo extends CI_Controller{public function __construct(){parent::__construct();}function loadUrologyContent(){$user_id=$this->input->post('user_id');$idpatient=$this->input->post('historial_id');$data['centro']=$this->input->post('centro');$data['user_id']=$user_id;$data['idpatient']=$idpatient;$sql_cnsling2="SELECT *  FROM  h_c_urology WHERE id_patient=0";$query_cnsling2=$this->db->query($sql_cnsling2);$data['query_cnsling']=$query_cnsling2;$this->load->view('admin/historial-medical1/urology/urology-content',$data);}function paginateUrology(){$idpatient=$this->input->post('idpatient');$data['idpatient']=$idpatient;$data['user_id']=$this->input->post('user_id');$data['centro']=$this->input->post('centro');$sql="SELECT *  FROM  h_c_urology WHERE id_patient=$idpatient ORDER BY id DESC";$query=$this->db->query($sql);$data['query_uro']=$query;$data['nb_cnuro']=$query->num_rows();$this->load->view('admin/historial-medical1/urology/paginate-number',$data);}function paginationData(){$page=$this->input->get('page');$idUser=$this->input->get('idUser');$data['idpatient']=$this->input->get('id_patient');$data['centro']=$this->input->get('centro');$data['user_id']=$idUser;$data['id_emp']=$page;$per_page=1;$start=($page-1)*$per_page;$sql="select * from h_c_urology WHERE id=$page";$data['query_uro']=$this->db->query($sql);$data['action_id']=1;$birthday=$this->db->select('date_nacer')->where('id_p_a',$this->input->get('id_patient'))->get('patients_appointments')->row('date_nacer');$this->load->view('getPatientAge');$data['edad']=getPatientAge($birthday);$this->load->view('admin/historial-medical1/urology/examen-fisico/form',$data);$this->load->view('admin/historial-medical1/urology/examen-fisico/footer',$data);}function loadSigno(){$data['edad']=$this->db->select('date_nacer')->where('id_p_a',$this->input->post('idpatient'))->get('patients_appointments')->row('date_nacer');$id_sig=$this->input->post('id_exam_fis');$sql="select *, id AS id_sig from  h_c_urology WHERE id=$id_sig";$query_uro=$this->db->query($sql);$data['signo_by_id']=$query_uro->result();$this->load->view('admin/historial-medical1/examen-fisico/signo_result',$data);}function formUpdate(){$update_info=$this->db->select('updated_by, updated_time')->where('id',$this->input->post('id_exam_fis'))->get('h_c_urology')->row_array();$update_time=date("d-m-Y H:i:s",strtotime($update_info['updated_time']));$updated_by=$this->db->select('name')->where('id_user',$update_info['updated_by'])->get('users')->row('name');echo $updated_by.', '.$update_time;}}