<div class="modal-header"id="background_"><button class="close"type="button"aria-hidden="true"data-dismiss="modal"title="Cierra"><i class="fa fa-times-circle"style="font-size:48px;color:red"></i></button><h3 class="h3 his_med_title">Reporte General</h3></div><div class="modal-body"id="background_"><div class="container"style="height:600px;overflow-y:scroll"><div class="row"><div id="paginationNumberReport"></div><hr class="prenatal-separator"><button class="btn btn-primary btn-xs"id="nuevo-formr">NUEVO REGISTRO</button><hr class="prenatal-separator"><div id="contentrp"></div><div id="hide-form-cirugia-reporte"><form class="form-horizontal"method="post"><input id="id_enf"type="hidden"value="0"> <input id="id_con"type="hidden"value="0"><div class="form-group"><label class="col-sm-2 control-label"><span style="color:red">*</span> REPORTE:</label><div class="col-sm-5"><select class="form-control select2"id="cirugia_reporte"><option></option><?php $sql="SELECT name FROM hc_cirugia_name order by id ASC";$query=$this->db->query($sql);foreach($query->result()as $row){echo "<option >".$row->name."</option>";} ?></select></div><div class="col-sm-2"><button class="btn btn-primary btn-xs"id="add-hist-actual"type="button"><span class="glyphicon glyphicon-plus-sign"></span> Historia Actual</button></div></div><div class="form-group"><label class="col-sm-2 control-label">DETALLE:</label><div class="col-sm-8"><div id="historial-actual-data"></div><textarea class="form-control clear-cirugia-toracia"id="cirugia_detalle"rows="12"></textarea></div></div><div class="form-group"><div class="col-sm-9 col-sm-offset-2"><button class="btn btn-md btn-success"id="save-cirugia-reporte"type="button"><i class="fa after-action fa-check"style="display:none;color:#00f;font-size:30px;position:absolute"></i> GUARDAR</button> <a class="btn btn-primary btn-md imprimir-cirugia-reporte"href="<?php echo base_url("printings/print_if_foto_c/c_t_0/$id_historial/$user_id/$centro_medico/r") ?>"style="display:none"target="_blank"><i class="fa fa-print"></i> Imprimir Vert.</a> <a class="btn btn-primary btn-md imprimir-cirugia-reporte"href="<?php echo base_url("printings/print_if_foto_c1/c_t_0_/$id_historial/$user_id/$centro_medico/r") ?>"style="display:none"target="_blank"><i class="fa fa-print"></i> Imprimir Horiz.</a></div></div></form></div></div></div></div><script>function addHistAct(){$("#save-cirugia-reporte").prop("disabled",!0),$.ajax({url:"<?php echo base_url(); ?>admin_medico/addHistAct",data:{id_user:<?=$user_id?>,id_patient:<?=$id_historial?>,centro:<?=$centro_medico?>},method:"POST",success:function(d){$("#historial-actual-data").html(d)}})}function assignIdEnfToReporte(){$.ajax({url:"<?php echo base_url(); ?>admin_medico/assignIdEnfToReporte",data:{id_user:<?=$user_id?>,id_patient:<?=$id_historial?>,centro:<?=$centro_medico?>},method:"POST",success:function(d){$("#id_enf").val(d)}})}function assignIdConToReporte(){$.ajax({url:"<?php echo base_url(); ?>admin_medico/assignIdConToReporte",data:{id_user:<?=$user_id?>,id_patient:<?=$id_historial?>,centro:<?=$centro_medico?>},method:"POST",success:function(d){$("#id_con").val(d),$("#save-cirugia-reporte").prop("disabled",!1)}})}function paginationNumberReport(){$.ajax({url:"<?php echo base_url(); ?>admin_medico/paginationNumberReport",data:{id_user:<?=$user_id?>,id_patient:<?=$id_historial?>,centro_medico:<?=$centro_medico?>},method:"POST",success:function(d){$("#paginationNumberReport").html(d)}})}$("#add-hist-actual").on("click",function(d){d.preventDefault(),addHistAct(),assignIdEnfToReporte(),assignIdConToReporte()}),$(".select2").select2({tags:!0}),paginationNumberReport(),$("#save-cirugia-reporte").on("click",function(d){d.preventDefault();var u=$("#cirugia_detalle").val(),e=$("#cirugia_reporte").val(),n=$("#id_enf").val(),i=$("#id_con").val();""!=e&&($("#save-cirugia-reporte").prop("disabled",!0),$("#save-cirugia-reporte").text("guardando..."),$.ajax({url:"<?php echo base_url(); ?>admin_medico/saveCirugiaReporte",data:{cirugia_reporte:e.trim(),cirugia_detalle:u,id_enf:n,id_con:i,id_cirugia_toracia_rep:0,id_user:<?=$user_id?>,id_patient:<?=$id_historial?>},method:"POST",success:function(d){paginationNumberReport(),$(".after-actionp").fadeIn("slow",function(){$(".after-actionp").delay(3e3).fadeOut()}),$(".imprimir-cirugia-reporte").show(),$("#save-cirugia-reporte").text("GUARDAR"),$("#save-cirugia-reporte").prop("disabled",!1)}}))})</script>