<?php foreach($cirugia_paginate->result()as $rowp){$inserted_time=date("d-m-Y H:i:s",strtotime($rowp->inserted_time));$doc=$this->db->select('name')->where('id_user',$rowp->id_user)->get('users')->row('name'); ?><form class="form-horizontal"id="change-bgd-pag-re"method="post"><h5 style="color:#ff0084">REGISTRO #<?=$page?>| creado por<?=$doc?>,<?=$inserted_time?></h5><div class="form-group"><label class="col-sm-2 control-label">FECHA:</label><div class="col-sm-3"><input class="form-control"id="fecha-rep-gnl"name="fecha-rep-gnl"value="<?=$inserted_time?>"></div></div><div class="form-group"><label class="col-sm-2 control-label">REPORTE:</label><div class="col-sm-5"><select class="form-control select2"id="cirugia_reportee"><?php echo"<option>$rowp->reporte</option>";echo "<option></option>";$sql="SELECT name FROM hc_cirugia_name  WHERE name !='$rowp->reporte' ORDER BY name DESC";$query=$this->db->query($sql);foreach($query->result()as $row){echo "<option >".$row->name."</option>";} ?></select></div><?php if($rowp->id_enf!=0&&$rowp->id_cond!=0){ ?><div class="col-sm-3"><div class="alert alert-info"style="text-align:center">Historia Actual esta anexada.</div></div><?php } ?></div><div class="form-group"><label class="col-sm-2 control-label">DETALLE:</label><div class="col-sm-8"><textarea class="form-control clear-cirugia-toracia"id="cirugia_detallee"rows="12"><?=$rowp->detalle?></textarea></div></div><div class="form-group"><div class="col-sm-9 col-sm-offset-3"><button class="btn btn-md btn-success"id="save-cirugia-generales"type="button"><i class="fa after-action fa-check"style="display:none;color:#00f;font-size:35px;position:absolute"></i> CAMBIAR</button> <a class="btn btn-md btn-primary"href="<?php echo base_url("printings/print_if_foto_c/$rowp->id/$id_patient/$user_id/$centro_medico/r") ?>"target="_blank"><i class="fa fa-print"></i> Imprimir Vert.</a> <a class="btn btn-md btn-primary"href="<?php echo base_url("printings/print_if_foto_c1/$rowp->id/$id_patient/$user_id/$centro_medico/r") ?>"target="_blank"><i class="fa fa-print"></i> Imprimir Horiz.</a></div></div></form><?php } ?><script>$(".select2").select2({tags:!0}),$(".load-cirugia").not(".registro-li").hide(),$("#save-cirugia-generales").on("click",function(e){e.preventDefault();var a=<?=$rowp->id?>,i=$("#cirugia_detallee").val(),r=$("#cirugia_reportee").val(),t=$("#fecha-rep-gnl").val();$.ajax({url:"<?php echo base_url(); ?>admin_medico/saveCirugiaReporte",data:{cirugia_reporte:r,cirugia_detalle:i.trim(),id_cirugia_toracia_rep:a,fecha:t},method:"POST",success:function(e){$(".after-action").fadeIn("slow",function(){$(".after-action").delay(3e3).fadeOut()})}})}),$('input[name="fecha-rep-gnl"]').mask("00-00-0000 00:00:00")</script>