<br><br><style>.box-tooltip2{color:#000;background:#fff;border-radius:20px;padding:6px;border:1px solid red;z-index:100000}.pagination2{display:inline-block}</style><h4 class="h4 his_med_title">conclusion diagnostica (<b><?=$count_conc?>regitro(s)</b>)</h4><?php $disable_cied='';if($count_conc>0){$hist_date=$this->db->select('hist_date')->where('id_pat',$id_historial)->get('saveHistPat')->row('hist_date');if($hist_date==date("Y-m-d")){$disable_cied="disabled";}else{$disable_cied="";}$i=1;foreach($concluciones as $row){$diagno_def=$this->db->select('diagno_def')->where('con_id_link',$row->id_cdia)->get('h_c_diagno_def_link')->row('diagno_def');$diag=$this->db->select('description')->where('idd',$diagno_def)->get('cied')->row('description');$user_c36=$this->db->select('name')->where('id_user',$row->inserted_by)->get('users')->row('name');$user_c37=$this->db->select('name')->where('id_user',$row->updated_by)->get('users')->row('name');$updated_time=date("d-m-Y H:i:s",strtotime($row->updated_time));$inserted_time=date("d-m-Y H:i:s",strtotime($row->inserted_time));$centro=$this->db->select('name')->where('id_m_c',$row->centro_medico)->get('medical_centers')->row('name'); ?><div class="pagination"><a class="con-pop-cl"data-target="#ver_con_d"data-toggle="modal"href="<?php echo base_url("admin_medico/show_con_by_id/$row->id_cdia/$id_historial/$user_id/$row->centro_medico/0") ?>"><?php echo $i;$i++; ?></a><br><br><div class="box-tooltip"style="display:none;position:absolute"><h4 style="color:green"><?=$centro?></h4><ul style="list-style:none"><li>Creado por<?=$user_c36?>,<?=$inserted_time?></li><li>Cambiado por<?=$user_c37?>,<?=$updated_time?></li><hr><li><strong>Diagnostico(s) Presuntivo o Definitivo (CIE10) </strong>:<?=$diag?></li><li><strong>Plan</strong> :<?=$row->plan?></li></ul></div></div><?php }}else{echo "<i><b>No hay registros</b></i>";} ?><div class="col-md-12 move_left"id="reload-cond"><hr class="prenatal-separator"><div class="col-md-12 form-horizontal"id="flashe"><div class="form-group"><label class="col-md-2 control-label"><span style="color:red"><strong>*</strong></span> Diagnostico(s) Presuntivo o Definitivo (CIE10):</label><div class="col-md-10"><input id="on_input_cied"autocomplete="off"class="form-control on_input_cied"<?=$disable_cied?>><br><div id="show-selected-cie10"style="display:none"></div><div class="cied_result"></div></div></div><div class="form-group otros-diagnos"style="display:none"><label class="col-md-2 control-label">Otros diagnosticos (opcional)</label><div class="col-md-10"><textarea class="form-control"id="otros_diagnos"></textarea></div></div><div class="form-group"><label class="col-md-2 control-label"><span style="color:red"><strong>*</strong></span> Plan</label><div class="col-md-10"><button class="btn btn-primary btn-sm"id="btnSpeechConDiagPlan"title="soporte solo para el navegador Chrome"type="button"><i aria-hidden="true"class="fa fa-microphone"></i></button> <span id="actionSpeechConDiagPlan"></span> <textarea class="form-control required-patient-inputs"id="plan"rows="6"></textarea></div></div><div class="form-group"><?php $id_seg=$this->db->select('seguro_medico')->where('id_p_a',$id_historial)->get('patients_appointments')->row('seguro_medico');$seg_n=$this->db->select('title')->where('id_sm',$id_seg)->get('seguro_medico')->row('title'); ?><label class="col-md-2 control-label">Procedimiento</label><div class="col-md-10"><button class="btn btn-primary btn-sm"id="btnSpeechConDiagProced"title="soporte solo para el navegador Chrome"type="button"><i aria-hidden="true"class="fa fa-microphone"></i></button> <span id="actionSpeechConDiagProced"></span> <textarea class="form-control"id="proced"rows="6"></textarea><?php if($id_seg){ ?><h4><?=$seg_n?></h4><select class="form-control select2"id="tarif-proced"><option value="">Seleccionar</option><?php $sqlpt="select id_tarif, procedimiento,monto FROM tarifarios WHERE id_seguro=$id_seg AND id_doctor=$user_id GROUP BY procedimiento";$proct=$this->db->query($sqlpt);foreach($proct->result()as $row){echo '<option value="'.$row->id_tarif.'">'.$row->procedimiento.' (RD$'.$row->monto.')</option>';} ?></select><?php }else{echo "<span style='color:red'>El campo de seguro del paciente esta vac√≠o</span>";} ?><div id="load-procedimientos-tarif"></div></div></div><div class="form-group"><label class="col-md-2 control-label">Evolucion (Paciente subsecuente):</label><div class="col-md-10"><button class="btn btn-primary btn-sm"id="btnSpeechConDiagEvo"title="soporte solo para el navegador Chrome"type="button"><i aria-hidden="true"class="fa fa-microphone"></i></button> <span id="actionSpeechConDiagEvo"></span> <textarea class="form-control required-patient-inputs"id="evolucion"rows="7"></textarea></div></div><div class="form-group"><label class="col-md-2 control-label"></label><div class="col-md-6"><input id="conclusion_radio"name="conclusion_radio"type="radio"value="Mejoria"checked> <label>Mejoria</label> <input id="conclusion_radio"name="conclusion_radio"type="radio"value="Empeorando"> <label>Empeorando</label> <input id="conclusion_radio"name="conclusion_radio"type="radio"value="No mejoria"> <label>No mejoria</label></div></div></div></div><script>var btnSpeechConDiagPlan=document.getElementById("btnSpeechConDiagPlan");btnSpeechConDiagPlan.onclick=function(){var e=document.getElementById("plan"),c=document.getElementById("actionSpeechConDiagPlan");runSpeechRecognition(e,c)};var btnSpeechConDiagProced=document.getElementById("btnSpeechConDiagProced");btnSpeechConDiagProced.onclick=function(){var e=document.getElementById("proced"),c=document.getElementById("actionSpeechConDiagProced");runSpeechRecognition(e,c)};var btnSpeechConDiagEvo=document.getElementById("btnSpeechConDiagEvo");function loadProcedimientoFacData(){$.ajax({url:"<?php echo base_url(); ?>admin_medico/loadProcedimientoFacData",data:{id_cd:0,patient:<?=$id_historial?>,user_id:<?=$user_id?>},method:"POST",success:function(e){$("#load-procedimientos-tarif").html(e)}})}btnSpeechConDiagEvo.onclick=function(){var e=document.getElementById("evolucion"),c=document.getElementById("actionSpeechConDiagEvo");runSpeechRecognition(e,c)},$("#tarif-proced").on("change",function(e){e.preventDefault(),""!=$(this).val()&&($("#load-procedimientos-tarif").html("Agregando..."),$.ajax({url:"<?php echo base_url(); ?>admin_medico/save_hist_tarif_pro",data:{id_tarif:$(this).val(),user_id:<?=$user_id?>,id_historial:<?=$id_historial?>,id_cd:0},method:"POST",success:function(e){$("#load-procedimientos-tarif").html(e)}}))}),$(".pagination").hover(function(){$(this).find(".box-tooltip").show()},function(){$(this).find(".box-tooltip").hide()});var timer=null;function doStuff1(){var e=$(".on_input_cied").val();$(".cied_result").fadeIn().html('<span style="font-size:12px" class="glyphicon glyphicon-refresh glyphicon-refresh-animate load"></span>'),""==e?$(".cied_result").hide():$.get("<?php echo base_url(); ?>admin_medico/on_input_cied?value="+e+"&id_pat=<?=$id_historial?>&tab=exam-1&user_id=<?=$user_id?>&centro_medico=<?=$centro_medico?>&id_cdia=&origine=0",function(e){$(".cied_result").html(e)})}$(".on_input_cied").keydown(function(){clearTimeout(timer),timer=setTimeout(doStuff1,1e3)}),doStuff1()</script>