<?php $showimppf='';foreach($query->result()as $rowid)if($query->result()==NULL){$showimppf='none';}else{$showimppf='';} ?><table class="table table-striped table-sum-pf"><thead><tr class="tr-header"style="background:#428bca"><th style="color:#fff">Procedimiento</th><th style="color:#fff">Precio</th><th style="color:#fff">Fecha</th><th style="color:#fff;display:<?=$showimppf?>"><?php if($id_cd!=0){ ?><ul class="nav navbar-nav"><li class="dropdown"><span class="dropdown-toggle"data-toggle="dropdown"style="cursor:pointer"><i class="fa fa-print"style="font-size:20px;color:#fff"></i><span class="caret"></span></span><ul class="dropdown-menu"><li><a>VERTICAL</a> <a style="cursor:pointer;color:#000"href="<?php echo base_url("printings/diag_p_vert/$id_cd/0/v") ?>"target="_blank">con foto</a> <a style="cursor:pointer;color:#000"href="<?php echo base_url("printings/diag_p_vert/$id_cd/1/v") ?>"target="_blank">Sin foto</a></li><li><a>HORIZONTAL</a> <a style="cursor:pointer;color:#000"href="<?php echo base_url("printings/diag_p_vert/$id_cd/0/h") ?>"target="_blank">Con foto</a> <a style="cursor:pointer;color:#000"href="<?php echo base_url("printings/diag_p_vert/$id_cd/1/h") ?>"target="_blank">Sin foto</a></li></ul></li></ul><?php } ?></th><th></th></tr></thead><tbody><?php if($query->result()!=NULL){$tot=0;foreach($query->result()as $row){$time=date("d-m-Y H:i:s",strtotime($row->time_created));$fac_val=$this->db->select('procedimiento,monto')->where('id_tarif',$row->id_tarif)->get('tarifarios')->row_array();$tot+=$fac_val['monto']; ?><tr><td style="font-size:10px"><?=$fac_val['procedimiento']?></td><td style="font-size:10px">RD$<?=number_format($fac_val['monto'],2)?></td><td style="font-size:10px"><?=$time?></td><td><a style="cursor:pointer"class="delete-fac-proc"id="<?=$row->id?>"title="Eliminar"><i class="fa fa-trash"style="color:red"></i></a></td></tr><?php } ?><tr><th style="font-size:10px">Total</th><th style="font-size:10px"id="value-sumpf">RD$<?=number_format($tot,2)?></th></tr><?php }else{echo "<tr>\n<th  style='font-size:10px'>No hay procedimento</th>\n</tr>";} ?></tbody></table><script>$(".delete-fac-proc").click(function(){var c=this,t=$(this).attr("id");$.ajax({type:"POST",url:"<?=base_url('admin_medico/deleteFacProc')?>",data:{id:t},success:function(t){$(c).closest("tr").css("background","tomato"),$(c).closest("tr").fadeOut(800,function(){$(this).remove()}),loadProcedimientoFacData()}})})</script>