<?php foreach($edit_estudios as $row_edit)$inserted_time=date("d-m-Y H:i:s",strtotime($row_edit->insert_date));$historial_id=$this->db->select('historial_id')->where('id_i_e',$row_edit->id_i_e)->get('h_c_indicaciones_estudio')->row('historial_id');$ussss=$this->db->select('name,area')->where('id_user',$row_edit->operator)->get('users')->row_array();$area=$ussss['area']; ?><div class="modal-header"id="background_"><button class="close"type="button"aria-hidden="true"data-dismiss="modal"title="Cierra"><i class="fa fa-times-circle"style="font-size:48px;color:red"></i></button><h3 class="text-center modal-title">Editar Estudios # <span class="round"><?=$row_edit->id_i_e?></span></h3><br><h5 class="text-center modal-title">Creado por :<?=$ussss['name']?>| fecha :<?=$inserted_time?></h5><div class="text-center col-md-12"><a class="btn-sm"href="<?php echo base_url("printings/print_estudios/$historial_id/$row_edit->id_i_e/$area/$row_edit->operator") ?>"style="cursor:pointer"target="_blank"title="Imprimir Antecedentes General"><i class="fa"style="font-size:24px">ï€¯</i></a></div></div><div class="disable-all modal-body"style="overflow-y:auto"><div class="col-md-6 col-md-offset-3"><div class="form-group"><label class="col-md-3 control-label"><font style="color:red">*</font>Estudios</label><div class="col-md-6"><select class="form-control select_est"id="study2"><?php foreach($estudios as $row){if($row_edit->estudio==$row->name){$selected="selected";}else{$selected="";} ?><option value="<?=$row->name?>"<?=$selected?>><?=$row->name?></option><?php } ?></select></div></div><div class="form-group"><label class="col-md-3 control-label"><font style="color:red">*</font>Parte del cuerpo</label><div class="col-md-6"><select class="form-control select_est"id="cuerpo2"><?php foreach($cuerpo as $rowp){if($row_edit->cuerpo==$rowp->name){$selected="selected";}else{$selected="";}echo"<option value='$rowp->name' $selected>$rowp->name</option>";} ?></select></div></div><div class="form-group"><label class="col-md-3 control-label">Lateralidad</label><div class="col-md-6"><input class="form-control"id="lateralidad2"min="1"type="number"value="<?=$row_edit->lateralidad?>"></div></div><div class="form-group"><label class="col-md-3 control-label">Observaciones</label><div class="col-md-6"><textarea class="form-control reset-est"id="observaciones2"><?=$row_edit->observacion?></textarea></div></div><div class="col-md-3 col-md-offset-3"><button class="btn btn-md btn-success"type="button"id="edit_estudios2"><i class="fa fa-floppy-o"aria-hidden="true"></i> Guardar</button></div></div></div><script>$(".select_est").select2({tags:!0}),$("#edit_estudios2").on("click",function(a){a.preventDefault();var e=$("#study2").val(),t=$("#cuerpo2").val(),o=$("#lateralidad2").val(),s=$("#observaciones2").val();return $.ajax({type:"POST",url:"<?=base_url('admin_medico/UpdateFormIndEst')?>",data:{study2:e,cuerpo2:t,lateralidad2:o,operator:"<?=$id_user?>",observaciones2:s,id:"<?=$row_edit->id_i_e?>"},cache:!0,success:function(a){alert("Cambio ha echo con exito !"),$("#edit_estudios").modal("hide")}}),!1})</script>