<div class="col-md-3"id="hidetableprint"><?php if($printlab!==null){ ?><table class="table"id="tableLabprintCurrent"><?php foreach($printlab as $row){$lab=$this->db->select('name')->where('id',$row->laboratory)->get('laboratories')->row('name'); ?><tr><td><a class="deletelabprint"id="<?=$row->id_lab?>"style="color:red;cursor:pointer"><i class="fa fa-trash-o"aria-hidden="true"></i></a></td><td><?=$lab?></td></tr><?php } ?></table><a class="currentprint"href="<?php echo base_url("printings/print_laboratorios/$historial_id/$areaid/$user_id") ?>"target="_blank"><i class="fa"style="font-size:24px;float:left">ï€¯</i></a><?php } ?></div><script>$(".currentprint").click(function(){$("#hidetableprint").hide()}),$(".deletelabprint").click(function(){$("#tableLabprintCurrent tr").length;var t=$("#tableLabprintCurrent td").closest("tr").length;1==t&&$(".currentprint").slideUp();var r=this,e=$(this).attr("id");$.ajax({type:"POST",url:"<?=base_url('admin_medico/DeleteHistLab')?>",data:{id:e},success:function(t){$(r).closest("tr").css("background","tomato"),$(r).closest("tr").fadeOut(800,function(){$(this).remove()}),allLaboratorios()}})})</script>