<table cellspacing="0"class="is_print_rect table table-bordered table-striped"id="paginate-rec-<?=$pag_rec?>"style="width:100%"><thead><tr style="background:#428bca"><th style="width:4px;color:#fff"><strong>Fecha</strong></th><th style="width:3px;color:#fff">Medica.</th><th style="width:5px;color:#fff">Present.</th><th style="width:1px;color:#fff">Frec.</th><th style="width:1px;color:#fff"><strong>Via</strong></th><th style="width:5px;color:#fff">Cant.(dias)</th><th style="width:1px;color:#fff">Oper.</th><td style="width:1px"><ul class="nav navbar-nav"><li class="dropdown"><span class="dropdown-toggle"data-toggle="dropdown"style="cursor:pointer"><i class="fa fa-print"style="font-size:20px;color:#fff"></i><span class="caret"></span></span><ul class="dropdown-menu"><li><a class="btn-sm print-me-rect"style="cursor:pointer;color:#000"title="Imprimir Con Formato Vertical"data-target="#print_rec_foto"data-toggle="modal"href="<?php echo base_url("printings/print_if_foto/$historial_id/$user_id/0/rec") ?>">Imprimir Con Formato Vertical</a></li><li><a class="btn-sm print-me-rect"style="cursor:pointer;color:#000"title="Imprimir Con Formato Horizontal"data-target="#print_if_foto_oriz"data-toggle="modal"href="<?php echo base_url("printings/print_if_foto_oriz/$historial_id/$user_id/0/rec") ?>">Imprimir Con Formato Horizontal</a></li></ul></li></ul></td><th style="width:1px;color:#fff">Edit.</th><th style="width:1px;color:#fff">Bor.</th></tr></thead><tbody><?php $cpt="";foreach($IndicacionesPrevias as $row){$op=$this->db->select('name')->where('id_user',$row->operator)->get('users')->row('name');$fecha=date("d-m-Y H:i:s",strtotime($row->insert_date));if($cpt==0){$cpt=1;$colorBg="#E0E5E6";}else{$cpt=0;$colorBg="#E0E5E6";}if($row->printing==1){$checked="checked";}else{$checked="";} ?><tr bgcolor="<?=$colorBg?>"><td><?=$fecha?></td><td><?=$row->medica?><br><i><u><?=$row->dosis?></u></i></td><td><?=$row->presentacion?></td><td><?=$row->frecuencia?></td><td><?=$row->via?><br><?=$row->oyo?></td><td><?php if($row->cantidad==0){echo "uso continuo";}else{echo $row->cantidad;} ?></td><td><?=$op?></td><td><?php if($row->operator==$user_id||$perfil=="Admin"){ ?><input class="check-recet-print"type="checkbox"value="<?=$row->id_i_m?>"<?=$checked?>><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td><td><?php if($row->operator==$user_id||$perfil=="Admin"){ ?><a class="btn-sm"style="cursor:pointer"title="Editar"data-target="#edit_recetas"data-toggle="modal"href="<?php echo base_url("admin_medico/edit_recetas/$row->id_i_m/$user_id") ?>"><span class="glyphicon glyphicon-pencil"></span></a><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td><td><?php if($row->operator==$user_id||$perfil=="Admin"){ ?><a class="delete-recetas"style="cursor:pointer"title="Eliminar recetas<?=$row->medica?>"id="<?=$row->id_i_m?>"><i class="fa fa-remove"style="color:red"></i></a><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td></tr><?php } ?></tbody></table>