<?php $per_page=1;if($perfil=="Admin"){$contition="";}else{$contition="id_user=$user_id AND";}$sql="SELECT id,inserted_time FROM hc_evaluacion_car_vas WHERE $contition patient=$id_historial ORDER BY id DESC";$cirugia_toracico=$this->db->query($sql);$count=$cirugia_toracico->num_rows();$pages=ceil($count/$per_page);if($pages>0){$regis_pages="$count registro(s)";}else{$regis_pages='no hay registro';} ?><i><?=$regis_pages?></i><div id="paginationh"><ul class="paginationh"><?php $i=1;foreach($cirugia_toracico->result()as $row){$timeins=date("d-m-Y H:i:s",strtotime($row->inserted_time));echo '<li class="paninate-li" id="'.$row->id.'">'.$timeins.'</li>';} ?><li class="load-cirugia"></li></ul></div><script>function refreshCardV(){$(".load-cirugia").not(".registro-li").fadeIn().html('<span style="font-size:18px" class="glyphicon glyphicon-refresh glyphicon-refresh-animate load"></span>'),$("#hide-form-cardiov").css({background:"#DCDCDC"})}$("#nuevo-formcv").click(function(r){$("#contentcv").hide(),$("input").val(""),$("textarea").val(""),$("#imprimir-eva-car").hide(),$("#save-eva-car").prop("disabled",!1),$("#hide-form-cardiov").show(),$("input:checked").removeAttr("checked"),r.preventDefault()}),$("#paginationh li").not(".load-cirugia").click(function(){$("#hide-form-cardiov").hide(),$("#paginationh li").not(".load-cirugia").css({border:"solid #0063DC 2px"}).css({color:"#0063DC"}),$(this).css({color:"#FF0084"}).css({border:"none"});var r=this.id;$("#contentcv").show();var e=<?=$user_id?>,a=<?=$id_historial?>,i=<?=$centro_medico?>;$("#contentcv").load("<?php echo base_url(); ?>/historialHeader/pagination_card_vascular?page="+r+"&user_id="+e+"&id_patient="+a+"&centro_medico="+i+"&perfil=<?=$perfil?>",refreshCardV())})</script>