<table cellspacing="0"class="table table-striped"><thead><tr style="background:#428bca"><th style="width:1px;color:#fff"><strong>Fecha</strong></th><th style="width:5px;color:#fff">Estudio</th><th style="width:5px;color:#fff">Parte del cuerpo</th><th style="width:1px;color:#fff"><strong>Lateralidad</strong></th><th style="width:5px;color:#fff">Observaciones</th><th style="width:1px;color:#fff">Operador</th><th style="width:1px;color:#fff">Borrar</th></tr></thead><tbody><?php $cpt="";foreach($IndicacionesPreviasEstudios->result()as $row){$estudio=$this->db->select('sub_groupo')->where('id_c_taf',$row->estudio)->get('centros_tarifarios')->row('sub_groupo');$op2=$this->db->select('name')->where('id_user',$row->operator)->get('users')->row('name');$cuerpo=$this->db->select('name')->where('id',$row->cuerpo)->get('type_body_parts')->row('name');$fecha=date("d-m-Y H:i:s",strtotime($row->insert_date));if($cpt==0){$cpt=1;$colorBg="#E0E5E6";}else{$cpt=0;$colorBg="#E0E5E6";} ?><tr bgcolor="<?=$colorBg?>"><td><?=$fecha?></td><td><?=$estudio?></td><td><?=$row->cuerpo?></td><td><?=$row->lateralidad?></td><td><?=$row->observacion?></td><td><?=$op2?></td><td><?php if($row->operator==$user_id||$perfil=="Admin"){ ?><a class="delete-estudio"id="<?=$row->id_i_e?>"style="cursor:pointer"title="Eliminar estudio<?=$row->estudio?>"><i class="fa fa-remove"style="color:red"></i></a><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td></tr><?php } ?></tbody></table><div class="fade modal"data-backdrop="static"id="edit_estudios"role="dialog"><div class="modal-dialog modal-lg"><div class="modal-content"></div></div></div><script>$(".delete-estudio").click(function(){if(confirm("Sure to delete ?")){var e=this,t=$(this).attr("id");$.ajax({type:"POST",url:"<?=base_url('admin_medico/deleteEstudiosOM')?>",data:{id:t},success:function(t){$(e).closest("tr").css("background","tomato"),$(e).closest("tr").fadeOut(800,function(){$(this).remove()})}})}})</script>