<?php $sql="select * from  orden_medica_estudios where id_sala=$idsala";$data2=$this->db->query($sql);if($data2->result()!=NULL){ ?><table cellspacing="0"class="table table-striped"><thead><tr style="background:#428bca"><th style="width:1px;color:#fff"><strong>Fecha</strong></th><th style="width:5px;color:#fff">Estudio</th><th style="width:1px;color:#fff">Ver</th><th style="width:1px;color:#fff">Operador</th><th style="width:1px;color:#fff">Edit</th><th style="width:1px;color:#fff">Borrar</th></tr></thead><tbody><?php $cpt="";foreach($data2->result()as $row){$op2=$this->db->select('name')->where('id_user',$row->operator)->get('users')->row('name');if($direction==1){$estudio=$this->db->select('sub_groupo')->where('id_c_taf',$row->estudio)->get('centros_tarifarios')->row('sub_groupo');}else{$estudio=$row->estudio;}$cuerpo=$this->db->select('name')->where('id',$row->cuerpo)->get('type_body_parts')->row('name');$fecha=date("d-m-Y H:i:s",strtotime($row->insert_date));if($cpt==0){$cpt=1;$colorBg="#E0E5E6";}else{$cpt=0;$colorBg="#E0E5E6";} ?><tr bgcolor="<?=$colorBg?>"><td><?=$fecha?></td><td><?=$estudio?></td><td title="Parte del cuerpo:<?=$row->cuerpo?>Lateralidad:<?=$row->lateralidad?>Observaciones:<?=$row->observacion?>"><i class="fa fa-eye"></i></td><td><?=$op2?></td><td><?php if($row->operator==$user_id||$perfil=="Admin"){ ?><a class="btn-sm"style="cursor:pointer"title="Editar"data-target="#edit_estudios_or_med2"data-toggle="modal"href="<?php echo base_url("admin_medico/edit_estudios_or_med/$row->id_i_e/$user_id/$direction") ?>"><span class="glyphicon glyphicon-pencil"></span></a><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td><td><?php if($row->operator==$user_id||$perfil=="Admin"){ ?><a class="deleteEstOrdMed"style="cursor:pointer"title="Eliminar estudio<?=$row->estudio?>"id="<?=$row->id_i_e?>"><i class="fa fa-remove"style="color:red"></i></a><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td></tr><?php } ?></tbody></table><?php }else{echo "<span style='color:red'>no hay</span>";} ?><script>$(".deleteEstOrdMed").click(function(){if(confirm("Sure to delete ?")){var e=this,t=$(this).attr("id");$.ajax({type:"POST",url:"<?=base_url('admin_medico/deleteEstudiosOM')?>",data:{id:t},success:function(t){$(e).closest("tr").css("background","tomato"),$(e).closest("tr").fadeOut(800,function(){$(this).remove()})}})}})</script>