<?php $sql="select * from  orden_medcia_lab where id_sala=$idsala";$data3=$this->db->query($sql);if($data3->result()!=NULL){ ?><table cellspacing="0"class="table table-striped"style="background:#fff"><thead><tr style="background:#428bca"><th style="width:1px;color:#fff"><strong>Fecha</strong></th><th style="width:5px;color:#fff">Laboratorio</th><th style="width:5px;color:#fff">Operator</th><th style="width:5px;color:#fff">Elim.</th></tr></thead><tbody><?php $cpt="";foreach($data3->result()as $row){$op3=$this->db->select('name')->where('id_user',$row->operator)->get('users')->row('name');if($direction==0){$lab=$this->db->select('name')->where('id',$row->laboratory)->get('laboratories')->row('name');}else{$lab=$this->db->select('sub_groupo')->where('id_c_taf',$row->laboratory)->get('centros_tarifarios')->row('sub_groupo');}$insert_time=date("d-m-Y H:i:s",strtotime($row->insert_time));if($cpt==0){$cpt=1;$colorBg="#E0E5E6";}else{$cpt=0;$colorBg="#E0E5E6";}if($row->printing==1){$checked="checked";}else{$checked="";} ?><tr bgcolor="<?=$colorBg?>"><td><?=$insert_time?></td><td><?=$lab?></td><td><?=$op3?></td><td><?php if($row->operator==$user_id||$perfil=="Admin"){ ?><a class="deletelabom"id="<?=$row->id_lab?>"style="color:red;cursor:pointer"title="Eliminar laboratorio<?=$row->laboratory?>"><i aria-hidden="true"class="fa fa-trash-o"></i></a><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td></tr><?php } ?></tbody></table><?php }else{echo "<span style='color:red'>no hay</span>";} ?><script>$(".deletelabom").click(function(){if(confirm("Sure to delete ?")){var e=this,t=$(this).attr("id");$.ajax({type:"POST",url:"<?=base_url('admin_medico/deleteLabsOM')?>",data:{id:t},success:function(t){$(e).closest("tr").css("background","tomato"),$(e).closest("tr").fadeOut(800,function(){$(this).remove()})}})}})</script>