<?php $sql="select * from  ord_med_med_gen where id_sala=$idsala";$data5=$this->db->query($sql);if($data5->result()!=NULL){ ?><table cellspacing="0"class="table table-striped"><thead><tr style="background:#428bca"><th style="width:1px;color:#fff"><strong>Fecha</strong></th><th style="width:5px;color:#fff">Medidas Generales</th><th style="width:5px;color:#fff">Descripcion</th><th style="width:1px;color:#fff"><strong>Intervalo De Realizacion</strong></th><th style="width:1px;color:#fff">Operador</th><th style="width:1px;color:#fff">Editar</th><th style="width:1px;color:#fff">Borrar</th></tr></thead><tbody><?php $cpt="";foreach($data5->result()as $row){$op2=$this->db->select('name')->where('id_user',$row->id_user)->get('users')->row('name');$fecha=date("d-m-Y H:i:s",strtotime($row->inserted_time));if($cpt==0){$cpt=1;$colorBg="#E0E5E6";}else{$cpt=0;$colorBg="#E0E5E6";} ?><tr bgcolor="<?=$colorBg?>"><td><?=$fecha?></td><td><?=$row->medidas_gen?></td><td><?=$row->descripcion_mg?></td><td><?=$row->intervalo_de_real?></td><td><?=$op2?></td><td><?php if($row->id_user==$user_id||$perfil=="Admin"){ ?><a class="btn-sm"style="cursor:pointer"data-target="#edit_medida_gnl"data-toggle="modal"href="<?php echo base_url("admin_medico/edit_medida_gnl/$row->idem/$user_id") ?>"title="Editar"><span class="glyphicon glyphicon-pencil"></span></a><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td><td><?php if($row->id_user==$user_id||$perfil=="Admin"){ ?><a class="delete_edit_medida_gnl"style="cursor:pointer"id="<?=$row->idem?>"><i class="fa fa-remove"style="color:red"></i></a><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td></tr><?php } ?></tbody></table><?php }else{echo "<span style='color:red'>no hay</span>";} ?><script>$(".delete_edit_medida_gnl").click(function(){if(confirm("Sure to delete ?")){var e=this,t=$(this).attr("id");$.ajax({type:"POST",url:"<?=base_url('admin_medico/deleteMedGnrl')?>",data:{id:t},success:function(t){$(e).closest("tr").css("background","tomato"),$(e).closest("tr").fadeOut(800,function(){$(this).remove()})}})}})</script>