<div style="overflow-x:auto"><table class="table table-striped"style="width:100%"><thead><tr style="background:#428bca"><th style="width:4px;color:#fff"><strong>Fecha</strong></th><th style="width:3px;color:#fff">Medica.</th><th style="width:1px;color:#fff">Ver</th><th style="width:1px;color:#fff">Bor.</th></tr></thead><tbody><?php foreach($IndicacionesPrevias->result()as $row){$op=$this->db->select('name')->where('id_user',$row->operator)->get('users')->row('name');$fecha=date("d-m-Y H:i:s",strtotime($row->insert_date));if($printing==1){$medname=$row->medica;}else{if(is_numeric($row->medica)){$medname=$this->db->select('name')->where('id',$row->medica)->get('emergency_medicaments')->row('name');}else{$medname=$row->medica;}} ?><tr><td><?=$fecha?></td><td><?=$medname?></td><td title="Presentation:<?=$row->presentacion?>Frecuencia:<?=$row->frecuencia?>Via:<?=$row->via?>Nota:<?=$row->nota?>"><i class="fa fa-eye"></i></td><td><?php if($row->operator==$user_id||$perfil=="Admin"){ ?><a class="delete-recetasnw"id="<?=$row->id_i_m?>"style="cursor:pointer"title="Eliminar recetas<?=$row->medica?>"><i class="fa fa-remove"style="color:red"></i></a><?php }else{echo "<i style='color:red' class='fa fa-ban'></i>";} ?></td></tr><?php } ?></tbody></table></div><div class="fade modal"id="edit_recetas_or_med"role="dialog"><div class="modal-dialog modal-lg"><div class="modal-content"></div></div></div><script>$("#edit_recetas_or_med").on("hidden.bs.modal",function(){$(this).removeData("bs.modal")}),$(".delete-recetasnw").click(function(){if(confirm("Sure to delete ?")){var e=this,t=$(this).attr("id");$.ajax({type:"POST",url:"<?=base_url('admin_medico/deleteRecetasOM')?>",data:{id:t},success:function(t){$(e).closest("tr").css("background","tomato"),$(e).closest("tr").fadeOut(800,function(){$(this).remove()})}})}})</script>