chRecognition(e,n)};var btnAyudEnf=document.getElementById("btnAyudEnf");function toSeconds(e){var n=e.split(":");return 3600*n[0]+60*n[1]}btnAyudEnf.onclick=function(){var e=document.getElementById("ajudante_enf"),n=document.getElementById("actionSpeechAyudEnf");runSpeechRecognition(e,n)},$(".select2").select2({tags:!0}),$(".hour").mask("00:00",{placeholder:"00:00"});var typingTimer,timer=null;function calHour(){var e=document.getElementById("hora_ini").value,n=document.getElementById("hora_fin").value,t=Math.abs(toSeconds(e)-toSeconds(n)),o=[Math.floor(t/3600),Math.floor(t%3600/60)];o=o.map(function(e){return e<10?"0"+e:e}).join(":"),document.getElementById("tiempo_quirurgico").value=""==e||""==n||"NaN:NaN"==o?"":o}var doneTypingInterval=1e3,$input=$("#hora_fin");function doneTyping(){calHour()}function paginationNumberQ(){$.ajax({url:"<?php echo base_url(); ?>historialHeader/showQuirurcaTabulation",data:{id_user:<?=$user_id?>,id_patient:<?=$id_historial?>,centro_medico:"<?=$centro_medico?>"},method:"POST",success:function(e){$("#paginationNumberQ").html(e)}})}$input.on("keyup",function(){clearTimeout(typingTimer),typingTimer=setTimeout(doneTyping,doneTypingInterval)}),$input.on("keydown",function(){clearTimeout(typingTimer)}),$("#hora_ini").on("keydown",function(){$("#hora_fin").val(""),$("#tiempo_quirurgico").val("")}),paginationNumberQ(),$("#save-quirurgica").submit(function(e){e.preventDefault(),$("#save-quirurgica-btn").prop("disabled",!0),$("#save-quirurgica-btn").text("guardando..."),$.ajax({url:"<?php echo base_url(); ?>historialHeader/saveQuirurgico",method:"POST",data:new FormData(this),contentType:!1,cache:!1,processData:!1,success:function(e){paginationNumberQ(),$(".after-actionq").fadeIn("slow",function(){$(".after-actionq").delay(3e3).fadeOut()}),$("#save-quirurgica-btn").text("GUARDAR"),$("#imprimir-cirugiq").show()}})})</script>