<?php $per_page=1;if($perfil=="Admin"){$contition="";}else{$contition="id_user=$user_id AND";}$sql="SELECT id,inserted_time FROM  hc_quirurgica WHERE $contition id_patient=$id_historial ORDER BY id DESC";$cirugia_toracico=$this->db->query($sql);$count=$cirugia_toracico->num_rows();$pages=ceil($count/$per_page);if($pages>0){$regis_pages="$count registro(s)";}else{$regis_pages='no hay registro';} ?><i><?=$regis_pages?></i><div id="paginationh"><ul class="paginationh"><?php $i=1;foreach($cirugia_toracico->result()as $row){$timeins=date("d-m-Y H:i:s",strtotime($row->inserted_time));echo '<li class="paninate-li" id="'.$row->id.'">'.$timeins.'</li>';} ?><li class="load-cirugia"></li></ul></div><script>function refreshQuirurgia(){$(".load-cirugia").not(".registro-li").fadeIn().html('<span style="font-size:18px" class="glyphicon glyphicon-refresh glyphicon-refresh-animate load"></span>'),$("#save-quirurgica-update").css({background:"#DCDCDC"})}$("#nuevo-formq").click(function(i){$("#contentq").hide(),$(".clear-cirugia-toracia").val(""),$("#imprimir-cirugiqe").hide(),$("#save-quirurgica-btn").prop("disabled",!1),$("#hide-form-quirurgia").show(),i.preventDefault()}),$("#paginationh li").not(".load-cirugia").click(function(){$("#hide-form-quirurgia").hide(),$("#paginationh li").not(".load-cirugia").css({border:"solid #0063DC 2px"}).css({color:"#0063DC"}),$(this).css({color:"#FF0084"}).css({border:"none"});var i=this.id;$("#contentq").show();var o=<?=$user_id?>,r=<?=$id_historial?>;$("#contentq").load("<?php echo base_url(); ?>/historialHeader/pagination_data_quirurca?page="+i+"&user_id="+o+"&id_patient="+r+"&centro_medico=<?=$centro_medico?>&perfil=<?=$perfil?>",refreshQuirurgia())})</script>