<?php $per_page=1;if($perfil=="Admin"){$contition="";}else{$contition="inserted_by=$user_id AND";}$sql="SELECT * FROM  hc_screening WHERE $contition id_patient=$id_historial ORDER BY id DESC";$cirugia_toracico=$this->db->query($sql);$count=$cirugia_toracico->num_rows();$pages=ceil($count/$per_page);if($pages>0){$regis_pages="$count registro(s)";}else{$regis_pages='no hay registro';} ?><i><?=$regis_pages?></i><div id="paginationh"><ul class="paginationh"><?php $i=1;foreach($cirugia_toracico->result()as $row){$timeins=date("d-m-Y H:i:s",strtotime($row->inserted_time));echo '<li class="paninate-li" id="'.$row->id.'">'.$timeins.'</li>';} ?><li class="load-cirugia"></li></ul></div><script>$("#nuevo-screening").click(function(e){e.preventDefault(),$("#contentScreen").hide(),$(".screeningInfo").hide(),$("#save-screening").prop("disabled",!1),$("#screeningSave").show(),$("#imprimir-screening").hide(),$("#screeningSave").trigger("reset")}),$("#paginationh li").not(".load-cirugia").click(function(){$("#screeningSave").hide(),$("#paginationh li").not(".load-cirugia").css({border:"solid #0063DC 2px"}).css({color:"#0063DC"}),$(this).css({color:"#FF0084"}).css({border:"none"});var e=this.id;$("#contentScreen").show();var n=<?=$user_id?>,i=<?=$id_historial?>,s=<?=$centro_medico?>;$("#contentScreen").load("<?php echo base_url(); ?>/historialHeader/pagination_data_screening?page="+e+"&user_id="+n+"&id_patient="+i+"&centro_medico="+s+"&perfil=<?=$perfil?>")})</script>