<style>td,th{text-align:left}.fa.fa-check,.fa.fa-history,em{color:red;font-size:11px;text-transform:lowercase}</style><?php $this->padron_database=$this->load->database('padron',TRUE);if($perfil=="Admin"){$controller="admin";}else{$controller="medico";}$today=date('Y-m-d'); ?><body><div class="col-md-12"><div class="row"><a href="<?php echo site_url("$controller/create_cita"); ?>"class="btn btn-primary btn-xs">Buscar/Crear Paciente</a><hr id="hr_ad"></div></div><div class="col-md-12"><div class="row"><div style="display:<?=$displayTotInfo?>"><select class="form-control select2"style="background:#e0e5e6;width:100%"id="by-doctor"><option value="0"><?=$countTotalCitaDocNum?></option><?php foreach($countTotalCitaDoc as $rowtd){$doctot=$this->db->select('name')->where('id_user',$rowtd->doctor)->get('users')->row('name');echo '<option value="'.$rowtd->doctor.'">Dr '.$doctot.' : '.$rowtd->Total.' cita(s) </option>';}echo "</select>";echo "<br/><br/>";echo "</div>";if($perfil=="Asistente Medico"){ ?><form action="<?php echo site_url("$controller/SeachCitaResultAs"); ?>"class="form-inline"><div class="col-md-3"><select class="form-control select2"style="background:#e0e5e6;width:100%"id="doctor"name="doctor"><option value="">Seleccione un medico</option><?php $sql="SELECT id_doctor FROM centro_doc_as WHERE id_asdoc =$iduser group by id_doctor";$query=$this->db->query($sql);foreach($query->result()as $row){$name=$this->db->select('name')->where('id_user',$row->id_doctor)->get('users')->row('name');echo '<option value="'.$row->id_doctor.'">Dr '.$name.'</option>';} ?></select></div><?php }else{ ?><form action="<?php echo site_url("$controller/SeachCitaResult"); ?>"class="form-inline"><div class="col-md-3"><select class="form-control select2"style="background:#e0e5e6;width:100%"name="centro"><?php foreach($centro_medico as $row){echo '<option value="'.$row->id_m_c.'">'.$row->name.'</option>';} ?></select></div><?php } ?><div class="col-md-9"style="background:#e0e5e6;border:1px so