 = encrypt_url(0);$patient=$this->db->select('nombre,photo,ced1,ced2,ced3,frecuencia')->where('id_p_a',$ver->id_patient)->get('patients_appointments')->row_array();$q=$this->db->select('id_patient')->where('id_patient',$ver->id_patient)->where('doctor',$ver->doctor)->where('centro',$ver->centro)->like('update_time',date("Y-m-d"))->get('rendez_vous');$sql_con="SELECT historial_id FROM h_c_enfermedad where filter_date='$today' AND historial_id=$ver->id_patient AND user_id=$ver->doctor AND centro_medico=$ver->centro";$atendido_con=$this->db->query($sql_con);$area_d=$this->db->select('title_area')->where('id_ar',$ver->area)->get('areas')->row("title_area");if($atendido_con->num_rows()>0){$atend="<i style='color:green' class='fa fa-check' aria-hidden='true'></i>";$title_hist="&#013 Hay una historial medica por hoy";$hist=1;}else{$atend="";$hist=0;$title_hist="";}$hist_h=encrypt_url($hist);$sqlcita="SELECT id_rdv FROM factura2 where id_rdv=$ver->id_apoint";$cita_con=$this->db->query($sqlcita);if($cita_con->num_rows()>0){$cita='#B8FFD3';$thay_fac1="<span title='cita facturada' style='color:green;font-size:11px'>RD$</span>";}else{$cita="";$thay_fac1="";}$freq="SELECT historial_id FROM h_c_enfermedad WHERE  historial_id=$ver->id_patient AND user_id=$ver->doctor group by filter_date";$cita_freq=$this->db->query($freq);if($cita_freq->num_rows()==0){$frecuencia_text="";$frecuencia_nbr="";}else if($cita_freq->num_rows()==1){$frecuencia_text="$cita_freq->num_rows vista";$frecuencia_nbr="$cita_freq->num_rows";}else{$frecuencia_text="$cita_freq->num_rows vistas";$frecuencia_nbr="$cita_freq->num_rows";} ?><tr style="background:<?=$cita?>"title="<?=$title_hist?>"><td><?php if($patient['photo']=="padron"){$photo=$this->padron_database->select('IMAGEN')->where('MUN_CED',$patient['ced1'])->where('SEQ_CED',$patient['ced2'])->where('VER_CED',$patient['ced3'])->get('fotos')->row('IMAGEN');if($photo){echo '<img width="75px"    src="data:image/jpeg;base64,'.base64_encode($photo).'" />';}else{echo '<img  style="width:75px;" src="'.base_url().'/assets/img/user.png"  />';}}else if($patient['photo']==""){ ?><img src="<?=base_url()?>/assets/img/user.png"width="75px"><?php }else{ ?><img src="<?=base_url()?>/assets/img/patients-pictures/<?php echo $patient['photo']; ?>"width="75px"><?php } ?></td><td><?=$ver->nec?></td><td style="text-transform:uppercase"><a href="<?php echo site_url("$controller/patient/$ver->id_patient/$ver->centro/$ver->doctor"); ?>"><?=$patient['nombre']?></a></td><td><?=$ver->fecha_propuesta?></td><td style="text-transform:uppercase"><?php $centro=$this->db->select('name,type')->where('id_m_c',$ver->centro)->get('medical_centers')->row_array(); ?><a href="<?php echo site_url("$controller/centro_medico/".$ver->centro); ?>"><?=$centro['name']?></a></td><td style="text-transform:uppercase"><?php $doctor=$this->db->select('name')->where('id_user',$ver->doctor)->get('users')->row('name');if($perfil=="Admin"){ ?><a href="<?php echo site_url("admin/doctor/".$ver->doctor); ?>"title="<?=$area_d?>"><?=$doctor?></a><?php }else{echo $doctor;} ?></td><td style="text-transform:uppercase"><?php if($perfil=="Admin"){$areaid_ad=encrypt_url(0); ?><a href="<?php echo site_url("$controller/historial_medical/$id_p_a_h/$id_user_h/$areaid_ad/$id_cm_h/$hist_h/$id_user_h/$from_view"); ?>"><i class="fa fa-history"aria-hidden="true"title="ir a la historia clinica"></i><?=$atend?><em title="<?=$frecuencia_text?>"><?=$frecuencia_nbr?></em></a><?php }else if($perfil=="Medico"){$areaid_ad=encrypt_url($area_id); ?><a href="<?php echo site_url("$controller/historial_medical/$id_p_a_h/$id_user_h/$areaid_ad/$id_cm_h/$hist_h/$id_user_h/$from_view"); ?>"><i class="fa fa-history"aria-hidden="true"title="ir a la historia clinica"></i><?=$atend?><em title="<?=$frecuencia_text?>"><?=$frecuencia_nbr?></em></a><?php }else if($perfil=="Asistente Medico"&&$centro['type']=="privado"){$doctor_h=encrypt_url($ver->doctor);$areaid=$this->db->select('area')->where('id_user',$ver->doctor)